constraint.cypher:
// We look only at files with the name pom.xml. This avoids scans of
// other Maven related files as dependency-reduced-pom.xml
MATCH (project:Maven:Project)-[:HAS_MODEL]->(pom:Pom) // {fileName: 'pom.xml'}
                             -[:DECLARES_DEPENDENCY]
                             ->(dependency { group:'com.buschmais.jqassistant.plugin',
                                             name: 'common' })
WHERE NOT pom.fileName =~ '.*dependency-reduced-pom.xml' AND
      dependency.version <> '${jqa-plugin-common.version}'
RETURN dependency

// We look only at files with the name pom.xml. This avoids scans of // other Maven related files as dependency-reduced-pom.xml MATCH (project:Maven:Project)-[:HAS_MODEL]->(pom:Pom ) -[:DECLARES_DEPENDENCY] ->(dependency { group: 'com.buschmais.jqassistant.core' }) WHERE NOT pom.fileName =~ '.*dependency-reduced-pom.xml' AND
dependency.name IN ['parent', 'analysis', 'report', 'shared', 'store', 'scanner', 'plugin'] AND dependency.version <> '${jqa-core.version}'
RETURN dependency
